import './temp.css'


/*function Temperature() {
    return (
        <section className="sensor">
        <Temperature />
        <h3 className="sensor__title">{props.name}</h3>
        <p>temperature = {temperature}</p>

        {props.type === "light" &&
            <input type="text" className="sensor__input"></input>
        }
    </section>
        
    );
}*/

function Sensor(props) {
    const addr = "http://192.168.247.99";


    const [temperature, setTemp] = useState(0);

    useEffect(() => {
        const timer = setInterval(() => {
            onClickEvent();

        }, 5000);

        // очистка интервала
        return () => clearInterval(timer);
    });

    async function onClickEvent() {
        try {

            const response = await fetch(`${addr}/temp`);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setTemp(data.temp);

            console.log(data);
            //alert("Успешно");
        }
        catch (error) {
            console.log(error);
        }
    }


    return (
        <section className="sensor">
            <Temperature />
            <h3 className="sensor__title">{props.name}</h3>
            <p>temperature = {temperature}</p>

            {props.type === "light" &&
                <input type="text" className="sensor__input"></input>
            }
        </section>
    );

}


export default Temperature;
