import './fan.css'
import { fanState, useEffect } from 'react';


    function Fan() {
        const addr = "http://192.168.247.99";
        async function onClickEvent() {
            try {
            
            const response = await fetch(`${addr}/fan?state=${(!fanState ? '1' : '0')}`);
            if (!response.ok) {
            throw new Error('Network response was not ok');
            }
            const data = await response.json();
            const button = document.getElementById('.button_fan');
            fanState = data.state;
            button.innerText = 'FAN: ' + (fanState ? 'ON':'OFF');
            button.classList.toggle('on', fanState);
            console.log(data);
            //alert("Успешно");
            }
            catch (error) {
            console.log(error);
            }
            }
        return (
            <div>
                <h1>Вентилятор</h1>
                <button id='button_fan' type='button' onClick={onClickEvent()}></button>
            </div>
        );
    }

export default Fan;
