async function Fan() {
    try {
    
    const response = await fetch(`${addr}/fan?state=${(!fanState ? '1' : '0')}`);
    if (!response.ok) {
    throw new Error('Network response was not ok');
    }
    const data = await response.json();
    fanState = data.state;
    button.innerText = 'FAN: ' + (fanState ? 'ON':'OFF');
    button.classList.toggle('on', fanState);
    console.log(data);
    //alert("Успешно");
    }
    catch (error) {
    console.log(error);
    }
    }

