import './switch.css'
import { fanState, useEffect } from 'react';


function Switch() {
    const addr = "http://192.168.247.99";
    const button = document.getElementById('button_switch');

    async function onClickEvent() {
        try {
            const response = await fetch(`${addr}/switch?state=${(!switchState ? '1' : '0')}`);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            switchState = data.state;
            button.innerText = (switchState ? 'ON' : 'OFF');
            button.classList.toggle('on', switchState);
            console.log(data);
            //alert("Успешно");
        }
        catch (error) {
            console.log(error);
        }
    }
    return (
        <div>
            <h1>Выключатель</h1>
            <button className='button' id='button_switch' type='button' onClick={() => onClickEvent()}>ON</button>
        </div>
    );
}

export default Switch;
